<script setup lang="ts">
import { onMounted, ref } from 'vue';

let svg = ref<SVGSVGElement | null>(null);

const emit = defineEmits(["currentRegion"]);

function hover(event: MouseEvent) {
    const target = event.target as SVGPathElement;
    const title = target.getAttribute("title");
    if (title) {
        emit("currentRegion", title);
    }
}

function loaded() {
    svg.value = document.querySelector("#peru-map") as SVGSVGElement;
    const regions = svg.value?.querySelectorAll("path");
    regions?.forEach((region) => {
        const bbox = region.getBBox();

        const centerX = bbox.x + bbox.width / 2;
        const centerY = bbox.y + bbox.height / 2;

        var text = document.createElementNS("http://www.w3.org/2000/svg", "text");
        text.setAttribute("x", String(centerX));
        text.setAttribute("y", String(centerY));
        text.setAttribute("text-anchor", "middle");
        text.setAttribute("dominant-baseline", "middle");
        text.setAttribute("fill", "white");
        text.setAttribute("font-size", "14");
        text.textContent = region.getAttribute("title") || "";

        svg.value?.appendChild(text);
    });
}
onMounted(() => {
    loaded()
})

</script>
